    uint64_t offsetsSize;
    {
        ZL_RESULT_OF(uint64_t) res = ZL_RC_popVarint(&in);
        ZL_GOTO_IF_ERR(_error, res);
        offsetsSize = ZL_RES_value(res);
    }
    uint8_t const* offsets = ZL_RC_ptr(&in);
    ZL_GOTO_IF(_error, offsetsSize > ZL_RC_avail(&in));
