    uint8_t const* extras = extrasBuffer;

    uint8_t const* const litsEnd    = lits + numLiterals;
    uint8_t const* const extrasEnd  = extras + extrasSize;
    uint8_t const* const offsetsEnd = offsets + offsetsSize;

